use approx::assert_ulps_eq;
use puruspe::utils::factorial;

#[test]
fn test_factorial() {
    for i in 0..90 {
        assert_ulps_eq!(factorial(i), (2..=i).map(|j| j as f64).product());
    }

    // Largest factorial that fits in a f64.
    assert_eq!(
        factorial(170),
        // Calculated with wolframalpha.com
        .0
    );
}
